var es = require('event-stream');
var plumber = require('./');

var badBoy = es.through(function(data) {
    if(data % 2 === 0)
        return this.emit('error', new Error(data));
    this.emit('data', data);
});

es.readArray([1,2,3,4,5,6])
    .pipe(plumber())
    .pipe(badBoy)
    .pipe(es.through(console.log))
